import React, {useEffect, useState} from 'react'
import StyledFirebaseAuth from 'react-firebaseui/StyledFirebaseAuth';
import {myFirebase} from '../lib/firebase'
import {AuthConfig, User, Employer} from '../model/model'
import {getUser, getEmployer} from '../lib/server'
import Router from 'next/router'

const Index: React.FC = (props) => {
    const [isSignedIn, setIsSignedIn] = useState(false);
    const [userEmployer, setUserEmployer] = useState<User | Employer | undefined>(undefined);
    useEffect(() => {
        myFirebase.auth().onAuthStateChanged(user => {
            if (user != null) {
                setIsSignedIn(true)
                setUserEmployer({
                    email: user.email,
                    id: user.uid,
                    firstName: user.displayName.substr(0, user.displayName.indexOf(' ')),
                    lastName: user.displayName.substr(user.displayName.indexOf(' ')+1),
                });
            }
        });
    });

    useEffect(() => {
        async function findUser() {
            const user = await getUser(userEmployer.id)
            const employer = await getEmployer(userEmployer.id)

            if (user['user_exists'] == undefined) {
                Router.replace('/dashboard_user')
            } else if (employer['employer_exists'] == undefined) {
                Router.replace('/dashboard_employer')
            } else {
                Router.replace('/signup')
            }
        }
        findUser()
    }, [isSignedIn])

    return (
        <>
            <StyledFirebaseAuth
                uiConfig={AuthConfig}
                firebaseAuth={myFirebase.auth()}/>
        </>
    );
};

export default Index;
