import React, {useEffect, useState} from 'react'
import StyledFirebaseAuth from 'react-firebaseui/StyledFirebaseAuth';
import {myFirebase} from '../lib/firebase'
import {AuthConfig, User, Employer} from '../model/model'
import {getUser, getEmployer} from '../lib/server'
import Router from 'next/router'

const Index: React.FC = (props) => {
    const [isSignedIn, setIsSignedIn] = useState(false);
    const [userEmployer, setUserEmployer] = useState<User | Employer | undefined>(undefined);
    useEffect(() => {
        myFirebase.auth().onAuthStateChanged(user => {
            if (user != null) {
                setIsSignedIn(true)
                setUserEmployer({
                    email: myFirebase.auth().currentUser.email,
                    id: myFirebase.auth().currentUser.uid,
                    firstName: myFirebase.auth().currentUser.displayName.substr(0, user.displayName.indexOf(' ')),
                    lastName: myFirebase.auth().currentUser.displayName.substr(user.displayName.indexOf(' ')+1),
                });
            }
        });
    });

    useEffect(() => {
        async function findUser() {
            if (myFirebase.auth().currentUser === null) {return}
            console.log('hit here')
            const user = await getUser(myFirebase.auth().currentUser.uid)
            const employer = await getEmployer(myFirebase.auth().currentUser.uid)

            if (user['user_exists'] == undefined) {
                Router.replace('/dashboard_user')
            } else if (employer['employer_exists'] == undefined) {
                Router.replace('/dashboard_employer')
            } else {
                Router.replace('/signup')
            }
        }
        findUser()
    }, [myFirebase.auth().currentUser])

    return (
        <div>
            <StyledFirebaseAuth
                uiConfig={AuthConfig}
                firebaseAuth={myFirebase.auth()}/>
        </div>
    );
};

export default Index;
