import React, { useEffect, useState } from 'react'
import { Steps } from 'antd';
import _ from 'lodash'
import {createUseStyles} from 'react-jss'
const {Step} = Steps;

const useStyles = createUseStyles({
    container: {
        flex: 1,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: 'rgba(0,0,0,0.5)',
        height: '100vh'
    }
})

export function Onboard() {
    const steps: {title: string, desc: string}[] = [{title: "Get Started", desc: "Add Personal Data"},
                    {title: "Add Socials", desc: "Add Social Networks"},
                    {title: "Take the Quiz", desc: "Finish Up and Get Evaluated"}]

    const [currentStep, changeStep] = useState(1);
    return (
        <div>
            <Steps current={currentStep}>
                {_.map(steps, ({title, desc}) => {
                    return (<Step title={title} description={desc}/>)
                })}
            </Steps>
        </div>
    );
}
