import React, {useEffect, useState} from 'react'
import StyledFirebaseAuth from 'react-firebaseui/StyledFirebaseAuth';
import {Button, Form, Input, Row, Radio, Card} from 'antd'
import {myFirebase} from '../lib/firebase'
import {User, Employer, NavBarStatus} from '../model/model'
import {getUser, getEmployer} from '../lib/server'
import Router from 'next/router'
import * as firebase from 'firebase/app'
import { Container } from '../components/Container';

const Index: React.FC = () => {
    const [status, setStatus] = useState<NavBarStatus>(NavBarStatus.Undecided)

    const onFinish = () => {

    }

    const onFinishFailed = () => {

    }

    const onClick = () => {
        Router.replace('/signup')
    }

    const updateStatus = ({newStatus}) => {
        setStatus(newStatus)
    }

    return (
        <Container>
            <Card title="Login" style={{paddingTop: '10%'}}>
                <Form
                    name="basic"
                    initialValues={{ remember: true }}
                    onFinish={onFinish}
                    onFinishFailed={onFinishFailed}
                    onValuesChange={updateStatus}
                    style={{marginBottom: 50}}
                >
                    <Form.Item
                        label="Email"
                        name="email"
                    >
                        <Input placeholder="Email"/>
                    </Form.Item>

                    <Form.Item
                        label="Password"
                        name="password"
                    >
                        <Input type="password" placeholder="Password"/>
                    </Form.Item>

                    <Form.Item label="You are" name="newStatus">
                        <Radio.Group value={status}>
                            <Radio.Button value={NavBarStatus.Employer}>Employer</Radio.Button>
                            <Radio.Button value={NavBarStatus.Designer}>Designer</Radio.Button>
                        </Radio.Group>
                    </Form.Item>

                    <Form.Item>
                        <Row justify="center">
                            <Button style={{width: '60%'}} type="primary" htmlType="submit">
                                Login
                            </Button>
                        </Row>
                        <Button onClick={onClick} style={{paddingTop: 10}} type="link">
                                Haven't made an account? Click here to sign up
                        </Button>
                    </Form.Item>
                </Form>
            </Card>
        </Container>
    );
};

export default Index;
