import React from 'react'
import {Typography, Anchor, Button} from 'antd'
import styled from 'styled-components'
const {Link} = Anchor

const Container = styled.div`
    display: flex;
    flex-direction: column;
`

const AboveText = styled(Typography.Text)`
    font-family: 'Mark Pro';
    opacity: 0.5;
    padding-bottom: 8px;
`

const BelowText = styled(AboveText)`
    opacity: 1.0;
    padding-bottom: 0px;
`

const LinkText = styled(Link)`
    opacity: 1.0;
    padding-bottom: 0px;
    font-family: 'Mark Pro';
    color: #13B0C3;
    background: rgba(0,0,0,0);
    border: none;
`

const ButtonBelow = styled(Button)`
    opacity: 1.0;
    padding: 0px;
    font-family: 'Mark Pro';
    color: #13B0C3;
    background: rgba(0,0,0,0);
    border: none;
`

interface TextAboveAnswerProps {
    above: string;
    below: string;
    link?: string;
    onClick?: () => void
}

const TextAboveAnswer: React.FC<TextAboveAnswerProps> = (props) => {
    const {above, below, link, onClick} = props
    return (
        <Container>
            <AboveText>{above}</AboveText>
            {link !== undefined ? 
                <Anchor>
                    <LinkText href={link} title={below}/>
                </Anchor>
                : onClick !== undefined ? 
                <ButtonBelow onClick={onClick}>{below}</ButtonBelow>
                : <BelowText>{below}</BelowText>
            }

        </Container>
    )
}

export default TextAboveAnswer
