import { User } from "../model/model";

const SERVER_BASE = 'https://sumpixel-server.herokuapp.com/'

async function request(url, params, method = 'GET') {

  const options = {
    method,
    mode: 'no-cors' as RequestMode,
    headers: {
      'Content-Type': 'application/json'
    }
  };

  if (params) {
    if (method === 'GET') {
      url += '?' + objectToQueryString(params);
    } else {
      options["body"] = JSON.stringify(params);
    }
  }

  const response = await fetch(SERVER_BASE + url, options);

  const result = await response.json();

  return result;

}

function objectToQueryString(obj) {
  return Object.keys(obj).map(key => key + '=' + obj[key]).join('&');
}

function generateErrorResponse(message) {
  return {
    status : 'error',
    message
  };
}

export const didCompleteQuiz = async (id: string) => {
    return await request('user-finished-quiz', {id: id})
}

export const getUser = async (id: string) => {
    return await request('get-user', {id: id})
}

export const createUser = async (user: User) => {
    return await request('create-user', user, 'POST')
}
