import React from 'react'
import Modal from 'react-modal'

interface PopupModalProps {
    visible: boolean;
    children: React.ReactNode
}
export const PopupModal: React.FC<PopupModalProps> = (props) => {
    return (
        <Modal
            animationType="fade"
            transparent={true}
            isOpen={props.visible}
            style={{
                top: 'auto',
                bottom: 'auto',
                left: 'auto',
                right: 'auto',
                padding: 0,
                border: 0,
            }}
        >
            <div style={{flex: 1, alignItems: 'center', justifyContent: 'center', backgroundColor: 'rgba(0,0,0,0.5)'}}>
                {props.children}
            </div>
        </Modal>
    );
};
