import React, {useEffect, useState} from 'react'
import StyledFirebaseAuth from 'react-firebaseui/StyledFirebaseAuth';
import {myFirebase} from '../lib/firebase'
import {User, Employer} from '../model/model'
import {getUser, getEmployer} from '../lib/server'
import Router from 'next/router'
import * as firebase from 'firebase/app'

const AuthConfig = {
    callbacks: {
        signInSuccessWithAuthResult: () => false
    },
    signInOptions: [
        firebase.auth.EmailAuthProvider.PROVIDER_ID
    ]
};

const Index: React.FC = () => {

    useEffect(() => {
        async function findUser() {
            if (myFirebase.auth().currentUser === null) {return}
            const user = await getUser(myFirebase.auth().currentUser.uid)
            const employer = await getEmployer(myFirebase.auth().currentUser.uid)

            if (user['user_exists'] == undefined) {
                Router.replace('/dashboard_user')
            } else if (employer['employer_exists'] == undefined) {
                Router.replace('/dashboard_employer')
            } else {
                Router.replace('/signup')
            }
        }
        findUser()
    }, [myFirebase.auth().currentUser])

    return (
        <div>
            <StyledFirebaseAuth
                uiConfig={AuthConfig}
                firebaseAuth={myFirebase.auth()}/>
        </div>
    );
};

export default Index;
