import React from 'react'
import {Typography, Anchor} from 'antd'
import styled from 'styled-components'
const {Link} = Anchor

const Container = styled.div`
    display: flex;
    flex-direction: column;
`

const AboveText = styled(Typography.Text)`
    font-family: 'Mark Pro';
    opacity: 0.5;
    padding-bottom: 8px;
`

const BelowText = styled(AboveText)`
    opacity: 1.0;
    padding-bottom: 0px;
`

interface TextAboveAnswerProps {
    above: string;
    below: string;
    link?: string;
}

const TextAboveAnswer: React.FC<TextAboveAnswerProps> = (props) => {
    const {above, below, link} = props
    return (
        <Container>
            <AboveText>{above}</AboveText>
            {link !== undefined ? 
                <Link href={link} title={below}/>
                : <BelowText>{below}</BelowText>
            }

        </Container>
    )
}

export default TextAboveAnswer
