import React from 'react'
import styled from 'styled-components'
import {UNIVERSAL_BACKGROUND, UNIVERSAL_COLOR} from '../../model/model'

const OnboardingFlowContainer = styled.div`
    background-color: ${UNIVERSAL_BACKGROUND}; 
    height: 100%;
    width: 100%;
    display: flex; 
    padding-top: 80px;
    justify-content: center;
`

const AbsolutePurple = styled.div`
    height: 50vh;
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    background-color: ${UNIVERSAL_COLOR};
`

export const OnboardingBackground: React.FC<{children: React.ReactNode}> = (props) => {
    return (
        <OnboardingFlowContainer>
            <AbsolutePurple/>
            {props.children}
        </OnboardingFlowContainer>
    )
}
