import React, {useState, useContext} from 'react'
import {PageHeader, Button, Tabs, message} from 'antd'
import {myFirebase} from '../../lib/firebase'
import Router from 'next/router'
import Loading from '../General/Loading'
import { UserContext } from '../../lib/UserProvider'
import styled from 'styled-components'

const {TabPane} = Tabs

const SumpixelHeader = styled(PageHeader)`
    background: #DAE2EF;

`

const LogoutButton = styled(Button)`

`

interface NavigationBarProps {
    subtitle: string
    footer: React.ReactNode
}

const NavigationBar: React.FC<NavigationBarProps> = (props) => {
    const {subtitle, footer} = props
    const {currentUser, changeUser} = useContext(UserContext)
    const [loading, setLoading] = useState(false)

    if (loading) {
        return (<Loading/>)
    }

    return (
        <SumpixelHeader 
                title={<img height={40} src={require('../../assets/sumpixel-logo.png')}/>}
                subTitle={subtitle}
                extra={[
                    <LogoutButton type="primary" onClick={async () => {
                        setLoading(true)
                        myFirebase.auth().signOut().then(() => {
                            changeUser(undefined)
                            setLoading(false)
                            Router.replace('/')
                        }).catch((error) => {
                            message.error(error.message)
                        })
                    }}>
                        Logout
                    </LogoutButton>
                ]}
                footer={footer} />
    )
}

export default NavigationBar
