import {Card, Form, Input, Button, Row, Typography, Col } from 'antd'
import {UserOutlined, LockOutlined} from '@ant-design/icons'
import React, {useState} from 'react'
import {NavBarStatus} from '../../model/model'
import Router from 'next/router'
import { BigBlackButton } from '../General/BigBlackButton'
import styled from 'styled-components'
import {SumpixelCard} from '../General/SumpixelCard'

const ForgotPasswordButton = styled(Button)`
    font-family: 'Mark Pro';
    color: #000000;
    opacity: 0.5;
    text-align: right;
    font-size: 12px;
`

interface LoginComponentProps {
    onFinish: (values) => void
}

const ForgotPasswordComponent: React.FC<LoginComponentProps> = (props) => {
    const {onFinish} = props

    const onClick = () => {
        window.analytics.track('Go to Sign Up');
        Router.replace('/signup')
    }

    return (
        <SumpixelCard>
            <Form
                hideRequiredMark={true}
                name="basic"
                initialValues={{ remember: true }}
                onFinish={onFinish}
            >
                <Typography.Text style={{paddingLeft: 30, paddingRight: 30}}>We'll send a link to reset your password for your account</Typography.Text>

                <Form.Item
                    label="Email"
                    name="email"
                    rules={[{ required: true, message: 'Email is required' }]}
                >
                    <Input placeholder="Email"/>
                </Form.Item>

                <Form.Item>
                    <Row justify="center">
                        <BigBlackButton style={{width: '60%', marginTop: 60}} type="primary" htmlType="submit">
                            Reset
                        </BigBlackButton>
                    </Row>
                </Form.Item>
            </Form>
        </SumpixelCard>
    )
}

export default ForgotPasswordComponent
